mapper.select:
select * from `member`
left join auth on `member`.authLevel = auth.authLevel 
where ID = #{ID}

mapper.insert:
insert into `member` set
ID = #{ID}, PW = #{PW}, nickname = #{nickname}, 
name = #{name}, email = #{email}, phoneNo = #{phoneNo}

mapper.select:
select * from `member`
left join auth on `member`.authLevel = auth.authLevel
where 1


		and uid = #{itemValue}


		and ID = #{itemValue}


		and nickname = #{itemValue}


		and email = #{itemValue}

select * from member left join auth on member.authLevel = auth.authLevel where 1 and delState = 0
and member.authLevel = #{authLevel}
and ID like concat('%' , #{searchKeyword}, '%')
and nickname like concat('%' , #{searchKeyword}, '%')
and (phoneNo like concat('%' , #{searchKeyword}, '%') or email like concat('%' , #{searchKeyword}, '%'))
order by uid desc limit #{page}, #{pageCnt}
