mapper.select:
select * from note, member
where 1


		and rUid = uid 
		and sUid = #{uid}
		and sDelState = 0


		and sUid = uid 
		and rUid = #{uid}
		and rDelState = 0


order by nid desc
limit #{page}, #{pageCnt}

select count(*) from note
where 1


		and sUid = #{uid}
		and sDelState = 0


		and rUid = #{uid}
		and rDelState = 0

select count(*) from note
where rUid = #{uid}
and `read` = false
